import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleRegister = async () => {
    try {
      await axios.post('http://localhost:5555/register', { username, password });
      alert('User registered successfully!');
    } catch (error) {
      alert('Registration failed. Please try again.');
    }
  };

  const handleLogin = async () => {
    try {
      await axios.post('http://localhost:5555/login', { username, password });
      setIsLoggedIn(true);
      alert('Login successful!');
    } catch (error) {
      alert('Invalid credentials. Please try again.');
    }
  };

  const handleViewScores = async () => {
    try {
      const response = await axios.get('http://localhost:5555/scores');
      console.log(response.data.scores);
    } catch (error) {
      console.error('Failed to fetch scores.');
    }
  };

  const handlePlayCard = async () => {
    try {
      const response = await axios.post('http://localhost:5555/play_card', {
        player_name: username,
        card_choice: 'some_card_choice', // Replace with actual card choice
      });
      console.log(response.data.message);
    } catch (error) {
      console.error('Failed to play card.');
    }
  };

  return (
    <div>
      <h2>Welcome to the Poker React App!</h2>
      {isLoggedIn ? (
        <div>
          <button onClick={handleViewScores}>View Scores</button>
          <button onClick={handlePlayCard}>Play Card</button>
        </div>
      ) : (
        <div>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button onClick={handleRegister}>Register</button>
          <button onClick={handleLogin}>Login</button>
        </div>
      )}
    </div>
  );
}

export default App;
