import React, { useState, useEffect } from 'react';
import axios from 'axios';

const GamePage = () => {
  const [displayedCard, setDisplayedCard] = useState(null);
  const [userTurn, setUserTurn] = useState(false);
  const [deck, setDeck] = useState([]);

  useEffect(() => {
    // Fetch the initial deck of cards when the component mounts
    axios.get('http://localhost:5555/deck')
      .then(response => {
        setDeck(response.data.deck);
      })
      .catch(error => {
        console.error('Error fetching deck:', error);
      });
  }, []);

  const startGame = () => {
    axios.get('http://localhost:5555/start_game')
      .then(response => {
        setDisplayedCard(response.data.displayed_card);
        setUserTurn(true);
      })
      .catch(error => {
        console.error('Error starting the game:', error);
      });
  };

  const playCard = () => {
    // Implement the logic to play a card
    // Update the 'userTurn' and 'displayedCard' state accordingly
    // Make a POST request to '/play_card' endpoint on the Flask server
  };

  return (
    <div>
      <h1>Poker Game</h1>
      {displayedCard && (
        <div>
          <h2>Displayed Card</h2>
          <p>{displayedCard.rank} of {displayedCard.suit}</p>
        </div>
      )}
      {userTurn && (
        <div>
          <button onClick={playCard}>Play Card</button>
        </div>
      )}
      <div>
        <h2>Full Deck</h2>
        <ul>
          {deck.map((card, index) => (
            <li key={index}>{card.rank} of {card.suit}</li>
          ))}
        </ul>
      </div>
      <div>
        <button onClick={startGame}>Start Game</button>
      </div>
    </div>
  );
};

export default GamePage;
