import React, { useState, useEffect } from 'react';
import './GamePage.css'; // Import the CSS file for styling

const GamePage = () => {
  const [userCards, setUserCards] = useState([]);
  const [computerCards, setComputerCards] = useState([]);
  const [displayedCard, setDisplayedCard] = useState('');
  const [availableCards, setAvailableCards] = useState({
    regular_cards: [
      "2H", "2D", "2C", "2S",
      "3H", "3D", "3C", "3S",
      "4H", "4D", "4C", "4S",
      "5H", "5D", "5C", "5S",
      "6H", "6D", "6C", "6S",
      "7H", "7D", "7C", "7S",
      "8H", "8D", "8C", "8S",
      "9H", "9D", "9C", "9S",
      "10H", "10D", "10C", "10S",
      "QH", "QD", "QC", "QS",
      "AH", "AD", "AC", "AS"
    ],
    special_cards: [
      "2H", "3H", "AH", "QH",
      "2D", "3D", "AD", "QD",
      "2C", "3C", "AC", "QC",
      "2S", "3S", "AS", "QS",
      "8H", "8D", "8C", "8S"  // Added special cards with rank '8' and suits 'H', 'D', 'C', 'S'
    ]
  });

  useEffect(() => {
    dealCards();
  }, []); // Empty dependency array ensures useEffect runs only once on component mount

  const dealCards = () => {
    // Deal 4 regular cards to the user and computer
    const userRegularCards = dealMultipleCards('regular_cards', 4);
    const computerRegularCards = dealMultipleCards('regular_cards', 4);

    // Deal 1 regular card to the display (ensuring it is not a special card)
    const displayedRegularCard = dealCard('regular_cards');

    setUserCards(userRegularCards);
    setComputerCards(computerRegularCards);
    setDisplayedCard(displayedRegularCard);
  };

  const dealMultipleCards = (cardType, count) => {
    const cards = [];
    for (let i = 0; i < count; i++) {
      cards.push(dealCard(cardType));
    }
    return cards;
  };

  const dealCard = (cardType) => {
    const cards = availableCards[cardType];
    const randomIndex = Math.floor(Math.random() * cards.length);
    const dealtCard = cards[randomIndex];

    // Remove the dealt card from available cards
    setAvailableCards(prevState => ({
      ...prevState,
      [cardType]: cards.filter(card => card !== dealtCard)
    }));

    return dealtCard;
  };

  return (
    <div className="game-container">
      <div className="user-section">
        <h3>Your Cards</h3>
        <div className="card-container">
          {userCards.map((card, index) => (
            <div key={index} className="card">
              {card}
            </div>
          ))}
        </div>
      </div>

      <div className="display-section">
        <h3>Displayed Card</h3>
        <div className="displayed-card">{displayedCard}</div>
      </div>

      <div className="computer-section">
        <h3>Computer Cards</h3>
        <div className="card-container">
          {computerCards.map((card, index) => (
            <div key={index} className="card">
              {card}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default GamePage;
