import React from 'react'

const handleNewGame= (e) => {
  e.preventDefault();
  window.location.href = '/PokerGame';
};

const handleViewScores= (e) => {
  e.preventDefault();
  window.location.href = '/Standing-Score';
};

const handleLogOut= (e) => {
  e.preventDefault();
  window.location.href = '/';
};

function ViewScores() {
  return (

    <div className='home-wrp'>
       <div className='right-sqr'>
       <div>
       <h1> Poker Game </h1>
       </div>
       <div className='right-sqr'>
        <h3> {userName} Score History</h3>
        <h4> Game Won: {gamesWon}</h4>
        <h4> Game Won: {gamesLost}</h4>
    </div>
      </div>

      <div className='left-sqr'>
        <button  onClick={handleNewGame}>New Game</button>
        <button className='active' onClick={handleViewScores}>View Scores</button>
        <button  onClick={handleLogOut}> Log Out</button>
      </div>
    </div>
  )
}

export default ViewScores
