import React, { useState, useEffect } from 'react';
import axios from 'axios';

const GamePage = () => {
  const [displayedCard, setDisplayedCard] = useState(null);

  useEffect(() => {
    // Fetch the displayed card when the component mounts
    fetchDisplayedCard();
  }, []);

  const fetchDisplayedCard = async () => {
    try {
      const response = await axios.get('http://localhost:5555/start_game');
      const { displayed_card: displayedCard } = response.data; // Update with the actual key in your response
      setDisplayedCard(displayedCard);
    } catch (error) {
      console.error('Error starting the game:', error);
    }
  };

  const drawCard = async () => {
    try {
      const response = await axios.post('http://localhost:5555/draw');
      const { card } = response.data;
      // Handle the drawn card as needed
    } catch (error) {
      console.error('Error drawing a card:', error);
    }
  };

  // Add more game-related functions as needed

  return (
    <div>
      <h1>Card Game</h1>
      {displayedCard && (
        <div>
          <p>Displayed Card: {displayedCard.rank} of {displayedCard.suit}</p>
        </div>
      )}
      <button onClick={drawCard}>Draw a Card</button>
      {/* Add more buttons and UI elements for the game */}
    </div>
  );
};

export default GamePage;
