// src/components/GamePage.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';

const GamePage = () => {
  const [userCards, setUserCards] = useState([]);
  const [computerCards, setComputerCards] = useState([]);
  const [displayedCard, setDisplayedCard] = useState(null);

  const initializeGame = async () => {
    try {
      // Fetch initial data from the server
      const deckResponse = await axios.get('http://localhost:5555/deck');
      const startGameResponse = await axios.get('http://localhost:5555/start_game');

      setUserCards(startGameResponse.data.userCards);
      setComputerCards(startGameResponse.data.computerCards);
      setDisplayedCard(startGameResponse.data.displayedCard);
    } catch (error) {
      console.error('Error initializing game:', error);
    }
  };

  const playCard = async (cardChoice) => {
    try {
      const response = await axios.post('http://localhost:5555/play_card', {
        player_name: 'user', // Assuming the player name is 'user'
        card_choice: cardChoice,
      });

      setUserCards(response.data.userCards);
      setComputerCards(response.data.computerCards);
      setDisplayedCard(response.data.displayedCard);
    } catch (error) {
      console.error('Error playing card:', error);
    }
  };

  useEffect(() => {
    initializeGame();
  }, []); // Run this effect only once on component mount

  return (
    <div className="game-container">
      <div className="card-container">
        {userCards.map((card, index) => (
          <div key={index} className="card" onClick={() => playCard(card)}>
            {card.rank} of {card.suit}
          </div>
        ))}
      </div>

      <div className="displayed-card">
        {displayedCard && (
          <div className="card">
            {displayedCard.rank} of {displayedCard.suit}
          </div>
        )}
      </div>

      <div className="card-container">
        {computerCards.map((card, index) => (
          <div key={index} className="card">
            {card.rank} of {card.suit}
          </div>
        ))}
      </div>
    </div>
  );
};

export default GamePage;
