import React from 'react'
import HomeLayout from './game/homeLayout'

const handleNewGame= (e) => {
  e.preventDefault();
  window.location.href = '/PokerGame';
};

const handleViewScores= (e) => {
  e.preventDefault();
  window.location.href = '/Standing-Score';
};

const handleLogOut= (e) => {
  e.preventDefault();
  window.location.href = '/';
};

function Home() {
  return (
    <div className='home-wrp'>
       <div className='right-sqr'>
       <div>
       <h1> Poker Game </h1>
       </div>
      </div>

      <div className='left-sqr'>
        <button className="btn" onClick={handleNewGame}>New Game</button>
        <button className="btn" onClick={handleViewScores}>View Scores</button>
        <button className="btn" onClick={handleLogOut}> Log Out</button>
      </div>
    </div>
  )
}

export default Home
